  // ãƒãƒªãƒ‡ãƒ¼ã‚·ãƒ§ãƒ³
  const validatedFields = CreateUserSchema.safeParse({
    name: formData.get('name'),
    email: formData.get('email'),
    department: formData.get('department'),
    position: formData.get('position'),
  })

  if (!validatedFields.success) {
